mapper.select:
select account
from app_user
where name = {Value}

select account
from app_user
where account = {Value}

mapper.update:
update app_user
set name={Name}
where id = {Id}

update app_user
set age={Age}
where id = {Id}

update app_user
set gender={Gender}
where id = {Id}

update app_user
set picture={Picture}
where id = {Id}

update app_org_user_role
set first_choice= {Flag}
where org_id = {OrgId}
  and user_id = {UserId}
  and role_id = {RoleId}

update app_org_user
set first_choice= {Flag}
where org_id = {OrgId}
  and user_id = {UserId}

update app_user
set password={Password}
where id = {Id}

update app_user
set password={Password}
where phone = {Phone}

update app_user
set password={Password}
where email = {Email}

update app_user
set phone={Phone}
where id = {Id}

update app_user
set email={Email}
where id = {Id}
